import React, {useState} from "react"
import Navbar from "../../Components/Navbar/Navbar"
import Footer from "../../Components/Footer/Footer.js"
import Axios from "axios"

const Register = () => {
    
    const[usernameReg, setUsernameReg] = useState("")
    const[emailReg, setEmailReg] = useState("")
    const[passwordReg, setPasswordReg] = useState("")
    const[confirmPasswordReg, setConfirmPasswordReg] = useState("")

    const register = () => {
        Axios.post("https://localhost3001/register", {
            username: usernameReg, email: emailReg, password: passwordReg
        }).then((response) => {
            console.log(response);
        })
    }

    return (
        <div>
            <Navbar />
            <div style={{ marginTop: "10px", float:"left", marginLeft:"16%", textAlign:"left"}}>
                <h1>Registar</h1>
                <br/>
                <input class="input" type="text" placeholder="Nome de utilizador" onChange={(e) => {setUsernameReg(e.target.value)}}></input>
                <br />
                <br />
                <input class="input" type="text" placeholder="Email" onChange={(e) => {setEmailReg(e.target.value)}}></input>
                <br />
                <br />
                <input class="input" type="password" placeholder="Palavra-passe" onChange={(e) => {setPasswordReg(e.target.value)}}></input>
                <br />
                <br />
                <input class="input" type="password" placeholder="Confirmar palavra-passe" onChange={(e) => {setConfirmPasswordReg(e.target.value)}}></input>
                <br />
                <br />
                <button onClick={register} class="mainButton">Registar</button>
            </div>
            <Footer />
        </div>
    )
}

export default Register
