import React, { useState } from "react"
import Navbar from "../../Components/Navbar/Navbar"
import Footer from "../../Components/Footer/Footer.js"
import Axios from "axios"

const Register = () => {

    const [username, setUsername] = useState("")
    const [email, setEmail] = useState("")
    const [password, setPassword] = useState("")
    const [confirmPassword, setConfirmPassword] = useState("")
    const [confirmPasswordStatus, setConfirmPasswordStatus] = useState("")

    const register = () => {
        if (password != confirmPassword) {
            setConfirmPasswordStatus("A palavra-passe não coincide com a confirmação.");
        }
        else {
            Axios.post("http://localhost:3001/register", {
                username: username, email: email, password: password
            }).then((response) => {
                console.log(response);
            })
        }
    }

    return (
        <div>
            <Navbar />
            <div style={{ marginTop: "10px", float: "left", marginLeft: "16%", textAlign: "left" }}>
                <h1>Registar</h1>
                <br />
                <input className="input" type="text" placeholder="Nome de utilizador" onChange={(e) => { setUsername(e.target.value) }}></input>
                <br />
                <br />
                <input className="input" type="text" placeholder="Email" onChange={(e) => { setEmail(e.target.value) }}></input>
                <br />
                <br />
                <input className="input" type="password" placeholder="Palavra-passe" onChange={(e) => { setPassword(e.target.value) }}></input>
                <br />
                <br />
                <p>{confirmPasswordStatus}</p>
                <input className="input" type="password" placeholder="Confirmar palavra-passe" onChange={(e) => { setConfirmPassword(e.target.value) }}></input>
                <br />
                <br />
                <button onClick={register} className="mainButton">Registar</button>
            </div>
            <Footer />
        </div>
    )
}

export default Register
